    private void btnAvancar10ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAvancar10ActionPerformed
        prbProgresso.setValue( prbProgresso.getValue() + 10 );
    }//GEN-LAST:event_btnAvancar10ActionPerformed

    private void btnZerarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnZerarActionPerformed
        prbProgresso.setValue( prbProgresso.getMinimum() );
    }//GEN-LAST:event_btnZerarActionPerformed

    private void btnAvancarRandomActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAvancarRandomActionPerformed
        int num_aleatorio = Globais.gerarNumero(20);
        
        prbProgresso.setValue( prbProgresso.getValue() + num_aleatorio );
    }//GEN-LAST:event_btnAvancarRandomActionPerformed

    private void btnMaximoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMaximoActionPerformed
        prbProgresso.setValue( prbProgresso.getMaximum() );
    }//GEN-LAST:event_btnMaximoActionPerformed

    private void btnDescontarRandomActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDescontarRandomActionPerformed
        int num_aleatorio = Globais.gerarNumero(20);

        prbProgresso.setValue( prbProgresso.getValue() - num_aleatorio );
    }//GEN-LAST:event_btnDescontarRandomActionPerformed

